import React, { Component } from "react";

class PostShopData extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      location: "",
      contact: "",
    };
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { name, location, contact } = this.state;
    return (
      <div>
        <form>
          <div>
            <input
              type="text"
              name="name"
              value={name}
              onChange={this.changeHandler}
            />
          </div>
          <div>
            <input
              type="text"
              name="location"
              value={location}
              onChange={this.changeHandler}
            />
          </div>
          <div>
            <input
              type="text"
              name="contact"
              value={contact}
              onChange={this.changeHandler}
            />
          </div>
          <buttom>Submit</buttom>
        </form>
      </div>
    );
  }
}

export default PostShopData;
